/**
 * Get MAZE sentences for a specific Despegando lesson
 */
export function getSpanishMazeSentencesForLesson(lesson: number | string): SpanishMazeSentence[] {
  const lessonNum = typeof lesson === 'string' ? parseInt(lesson.replace('L', '')) : lesson;
  return DESPEGANDO_MAZE_SENTENCES[lessonNum] || [];
}

/**
 * Get only illustratable sentences for Draw & Write worksheets
 */
export function getSpanishIllustratableSentences(lesson: number | string): SpanishMazeSentence[] {
  const sentences = getSpanishMazeSentencesForLesson(lesson);
  return sentences.filter(s => s.illustratable);
}

/**
 * Get sentences for lessons up to and including specified lesson (cumulative)
 */
export function getSpanishCumulativeSentences(maxLesson: number | string): SpanishMazeSentence[] {
  const maxNum = typeof maxLesson === 'string' ? parseInt(maxLesson.replace('L', '')) : maxLesson;
  const sentences: SpanishMazeSentence[] = [];
  for (let i = 1; i <= maxNum; i++) {
    sentences.push(...getSpanishMazeSentencesForLesson(i));
  }
  return sentences;
}
